var bot = require('bot.js'),
t = require('plugins/translate.js'),
g = require('plugins/get.js'),
$ = require('jquery'),
_ = require('underscore');

var say = function(to, msg) {
    bot.client.say(to, msg);
},
s = say;

bot.onTrigger(this, 'e', function(from, to, msg) {
    try {
        console.log('%s is trying to run %s...', from, msg);
        var e = eval(msg);
        if (typeof e !== 'undefined') {
            bot.client.say(to, e);
        }
    } catch(err) {
        console.log(err);
    }
});

exports.destruct = function() {
    bot.removeListeners(this);
};

