var bot = require('bot.js');

var from = exports.from = {};
var msg = exports.msg = [];

bot.addListener(this, 'message', function(f, t, m) {
    var bundle = {
        time: new Date(),
        from: f,
        to: t,
        msg: m
    };
    if (!from[f]) {
        from[f] = [];
    }
    from[f].push(bundle);
    msg.push(bundle);
});

